import React, {Component} from 'react'
import ApiService from '../ApiService'

import Button from '@material-ui/core/Button'
import Typography from '@material-ui/core/Typography'
import TextField from '@material-ui/core/TextField'
import Select from '@material-ui/core/Select'
import InputLabel from '@material-ui/core/InputLabel'

class AddUser extends Component{
    constructor(props){
        super(props)

        this.state={
            userName: '',
            password: '',
            firstName: '',
            lastName: '',
            gender:'',
            salary:'',
            message:null
        }
    }
    onChange = (e) => {
        this.setState({
            [e.target.name]:e.target.value
        })
    }

    render(){
        return(
            <div>
                <Typography variant='h6' style={style}>사용자 추가</Typography>
                <form method='post'>
                    <TextField style={style} type='text' id='userName' name='userName' label='사용자이름' margin='none' fullWidth value={this.state.userName} onChange={this.onChange} />
                    <TextField style={style} type='password' id='password' name='password' label='패스워드' margin='none' fullWidth value={this.state.userName} onChange={this.onChange} />
                    <TextField style={style} type='text' id='firstName' name='firstName' label='이름' margin='none' fullWidth value={this.state.userName} onChange={this.onChange} />
                    <TextField style={style} type='text' id='lastName' name='lastName' label='성' margin='none' fullWidth value={this.state.userName} onChange={this.onChange} />
                    <Select label='성별' value={this.state.gender} onChange={this.onChange}>
                        <option aria-label='None' value=''>-- 선택 --</option>
                        <option value={'M'}>남자</option>
                        <option value={'F'}>여자</option>
                        </Select>
                    
                    <TextField style={style} type='text' id='salary' name='salary' label='급여' margin='none' fullWidth value={this.state.userName} onChange={this.onChange} />
                </form>
            </div>
        )
    }
}
const style = {
    display:'flex',
    justifyContent:'center',
    marginTop:'40px'
}
export default AddUser
