import React, {Component} from 'react'
import ApiService from '../route/ApiService'

import Table from '@material-ui/core/Table'
import TableBody from '@material-ui/core/TableBody'
import TableHead from '@material-ui/core/TableHead'
import TableRow from '@material-ui/core/TableRow'
import TableCell from '@material-ui/core/TableCell'
import Button from '@material-ui/core/Button'
import Typography from '@material-ui/core/Typography'
import CreateIcon from '@material-ui/icons/Create'
import EditIcon from '@material-ui/icons/Edit'
import DeleteIcon from '@material-ui/icons/Delete'

class UserList extends Component{
    constructor(props){
        super(props)

        this.state={
            users:[],
            message:null,

        }
        console.log("constructor Run")
    }
    componentDidMount(){
        console.log('componentDidMount Run')
        this.reloadUserList()
    }

    reloadUserList(){
        ApiService.fetchUsers()
                  .then(res =>{
                    this.setState({users:res.data})
                  })
                  .catch(err =>{
                      console.error('reloadUserList() 에러!',err)
                  })
    
    }

    componentWillUnmount(){
        console.log('componentWillUnmount Run')
    }

    render(){
        console.log('render Run')
        console.log(this.state.users)
        return(
            <div>
                <Typography variant='h4' style={style}>User List</Typography>
                <Button variant="contained" color='secondary' type='button'>추가</Button>
                <Table>
                    <TableHead>
                        <TableRow>
                            <TableCell>ID</TableCell>
                            <TableCell>UserName</TableCell>
                            <TableCell>FirstName</TableCell>
                            <TableCell>LastName</TableCell>
                            <TableCell>Gender</TableCell>
                            <TableCell>Salary</TableCell>
                            <TableCell align='center'>Edit</TableCell>
                            <TableCell align='center'>Delete</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {this.state.users.map(user => 
                        <TableRow key={user.id}>
                            <TableCell align='center'>{user.id}</TableCell>
                            <TableCell align='center'>{user.userName}</TableCell>
                            <TableCell align='center'>{user.firstName}</TableCell>
                            <TableCell align='center'>{user.lastName}</TableCell>
                            <TableCell align='center'>{user.gender}</TableCell>
                            <TableCell align='center'>{user.salary}</TableCell>
                            <TableCell align='center'><input type="button" value="수정"/></TableCell>
                            <TableCell align='center'><input type="button" value="삭제"/></TableCell>
                        </TableRow>
                    )}
                    </TableBody>
                </Table>
            </div>
        )
    }
}

const style = {
    display:'flex',
    justifyContent:'center'
}
export default UserList;
